def solution(stdin: str) -> str:
    # Parse input
    n = int(stdin.readline().strip())
    a = [int(x) for x in stdin.readline().strip().split()]

    # Check if n is even
    if n % 2!= 0:
        return "Error: n must be even"

    # Sort a in descending order
    a.sort(reverse=True)

    # Initialize dp
    dp = [[0] * (n + 1) for _ in range(n + 1)]

    # Fill dp
    for i in range(n):
        for j in range(i + 1, n):
            if a[i] == a[j]:
                dp[i][j] = dp[i][j - 1] + 1
            else:
                dp[i][j] = dp[i][j - 1] + dp[i + 1][j]

    # Find the minimum number of problems to form n/2 teams
    ans = n
    for i in range(n // 2):
        for j in range(i + 1, n // 2 + 1):
            ans = min(ans, dp[i][j] + dp[n - j - 1][n - i - 1])

    return str(ans)

