    # Reading input
    N = int(stdin.readline().strip())
    A = list(map(int, stdin.readline().strip().split()))

    # Building a graph
    G = {}
    for i in range(N):
        for j in range(i+1, N):
            if A[i] == A[j]:
                continue
            if A[j] in G.get(A[i], []):
                continue
            G.setdefault(A[i], []).append(A[j])

    # Finding the starting node
    start = None
    for node in G:
        if len(G[node]) == 0:
            start = node
            break

    # Finding the order of nodes
    order = []
    while start is not None:
        order.append(start)
        next_node = G[start].pop()
        if len(G[next_node]) == 0:
            start = None
        else:
            start = next_node

    # Printing the order
    return''.join(map(str, order))

