            graph[a] = [b]
        else:
            graph[a].append(b)
        if b not in graph:
            graph[b] = [a]
        else:
            graph[b].append(a)

    # DFS to find all paths
    paths = []
    def dfs(start, end, path):
        if start == end:
            paths.append(path)
            return
        if start not in graph:
            return
        for neighbor in graph[start]:
            if neighbor not in path:
                dfs(neighbor, end, path + [neighbor])

    dfs(1, num_vertices, [1])
    return len(paths)

