                if grid[i][j] == '*' or grid[i][j] == '.':
                    return False
        return True

    stars = []
    for i in range(1, n+1):
        for j in range(1, m+1):
            if grid[i-1][j-1] == '*':
                s = 1
                while i+s-1 <= n and j+s-1 <= m and grid[i+s-1][j+s-1] == '*':
                    s += 1
                if is_valid(i, j, s, grid):
                    stars.append((i, j, s))

    if len(stars) > n*m:
        return "-1"
    else:
        return str(len(stars)) + "\n" + "\n".join(map(lambda x: " ".join(map(str, x)), stars))


