    graph = [[] for _ in range(n)]
    for i in range(n):
        for j in range(i+1, n):
            if a[i][0] == a[j][1] or a[i][1] == a[j][0]:
                graph[i].append(j)
                graph[j].append(i)

    # find cycles
    visited = [False] * n
    cycles = []
    def dfs(v, parent, cycle):
        nonlocal visited, cycles
        visited[v] = True
        cycle.append(v)
        for u in graph[v]:
            if u == parent:
                continue
            if not visited[u]:
                dfs(u, v, cycle)
            elif u not in cycle:
                cycle.extend(cycle[cycle.index(u):])
        if len(cycle) > 1:
            cycles.append(cycle)

    for i in range(n):
        if not visited[i]:
            dfs(i, -1, [])

    # restore permutation
    p = list(range(n))
    for cycle in cycles:
        if len(cycle) == n:
            continue
        cycle_set = set(cycle)
        cycle_dict = {v: i for i, v in enumerate(cycle)}
        for i in range(n):
            for j in range(i+1, n):
                if a[cycle_dict[i]][0] == cycle_dict[j]:
                    p[i], p[j] = p[j], p[i]
                elif a[cycle_dict[i]][1] == cycle_dict[j]:
                    p[i], p[j] = p[j], p[i]
        for i in range(n):
            if p[i] in cycle_set:
                continue
            for j in cycle:
                if a[cycle_dict[i]][0] == j:
