def solution(stdin: str) -> str:
    # read input
    n, m = map(int, stdin.readline().split())
    k = list(map(int, stdin.readline().split()))
    offers = []
    for _ in range(m):
        d, t = map(int, stdin.readline().split())
        offers.append((d, t))

    # initialize variables
    burles = 0
    order_times = [0] * n
    for t in range(n):
        burles += k[t] * 2
        if offers and offers[0][1] == t:
            burles -= 1
        order_times[t] = burles

    # binary search
    left, right = 0, burles
    while left < right:
        mid = (left + right) // 2
        can_buy = True
        for t in range(n):
            if k[t] > mid // 2:
                can_buy = False
                break
            if order_times[t] > mid:
                can_buy = False
                break
            if offers and offers[0][1] == t:
                burles -= 1
        if can_buy and burles >= 1:
            right = mid
        else:
            left = mid + 1
    return left

