def solution(stdin: str) -> str:
    n, k = map(int, stdin.readline().split())
    a = list(map(int, stdin.readline().split()))
    edges = []
    for i in range(n-1):
        u, v = map(int, stdin.readline().split())
        edges.append((u-1, v-1))

    # Create a graph using adjacency list
    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    # BFS to find all pairs of vertices with distance more than k
    visited = [False] * n
    queue = [(0, -1, -1)]  # (vertex, parent, distance)
    pairs = set()
    while queue:
        vertex, parent, dist = queue.pop(0)
        if visited[vertex]:
            continue
        visited[vertex] = True
        if dist > k:
            pairs.add((parent, vertex))
        for neighbor in graph[vertex]:
            if neighbor!= parent:
                queue.append((neighbor, vertex, dist+1))

    # Find the maximum total weight of the subset
    weights = [0] * n
    visited = [False] * n
    for u, v in pairs:
        if u!= -1:
            weights[u] += a[v]
            visited[u] = True
        if v!= -1 and not visited[v]:
            weights[v] += a[u]
            visited[v] = True
    max_weight = max(weights)
    return str(max_weight)

