        d, u = heappop(pq)
        if d > dist[u]:
            continue
        for v, w in graph[u]:
            if dist[u] + w < dist[v]:
                dist[v] = dist[u] + w
                parent[v] = u
                heappush(pq, (dist[v], v))
    if dist[n]!= dist[1]:
        return 'Impossible'
    ans = 0
    visited = [False] * (n + 1)
    for i in range(1, n + 1):
        if not visited[i]:
            q = [i]
