    n, q = map(int, stdin.split())
    coins = list(map(int, stdin.split()))
    queries = list(map(int, stdin.readlines()))

    # binary search for minimum number of coins to obtain query value
    def binary_search(query):
        lo, hi = 0, n
        while lo < hi:
            mid = (lo + hi) // 2
            if coins[mid] >= query:
                hi = mid
            else:
                lo = mid + 1
        return lo

    # answer for each query
    ans = []
    for query in queries:
        if query == 0:
            ans.append(0)
        else:
            coins_needed = binary_search(query)
            if coins_needed == n or coins[coins_needed] < query:
                ans.append(-1)
            else:
                ans.append(coins_needed)

    return '\n'.join(map(str, ans))

