    def count_occupied_tables(s: str) -> int:
        count = 0
        for i in range(len(s)):
            if s[i] == '1':
                count += 1
        return count

    def find_max_distance(s: str, k: int) -> int:
        max_dist = 0
        for i in range(len(s)):
            if s[i] == '1':
                for j in range(i+1, len(s)):
                    if s[j] == '1':
                        dist = abs(i - j)
                        if dist > max_dist:
                            max_dist = dist
        return max_dist

    def replace_tables(s: str, k: int) -> str:
        occupied_tables = []
        for i in range(len(s)):
            if s[i] == '1':
                occupied_tables.append(i)
        occupied_tables.sort()
        for i in range(len(occupied_tables)):
            for j in range(i+1, len(occupied_tables)):
                if occupied_tables[j] - occupied_tables[i] <= k:
                    return s
        return s

    def count_replacements(s: str, k: int) -> int:
        count = 0
        for i in range(len(s)):
            if s[i] == '0':
                s_new = s[:i] + '1' + s[i+1:]
                if replace_tables(s_new, k) == s_new:
                    count += 1
        return count

