        # if there is only one occupied table, return 1
        if occupied_tables == 1:
            print(1)
            continue
        # if the max distance between occupied tables is less than or equal to k, return 1
        if find_max_distance(s, k) <= k:
            print(1)
            continue
        # otherwise, count replacements
        print(count_replacements(s, k))

