        return "5" if arr[0] == 0 else str(arr[0] * 2)

    # initialize variables
    a = b = c = 0
    left = 0
    right = sum(arr)

    # binary search to find the maximum value of a
    while left < right:
        mid = (left + right) // 2
        if is_valid(arr, mid, a, b, c, n):
            right = mid
        else:
            left = mid + 1
    a = right

    # binary search to find the minimum value of b
    left = 0
    right = n - a - c
    while left < right:
        mid = (left + right) // 2
        if is_valid(arr, a, mid, b, c, n):
            right = mid
        else:
            left = mid + 1
    b = right

    # binary search to find the minimum value of c
    left = 0
    right = n - a - b
    while left < right:
        mid = (left + right) // 2
        if is_valid(arr, a, b, c=mid, n=n):
            right = mid
        else:
            left = mid + 1
    c = right

    # calculate and return the maximum possible value of sum_1
    return str(a + b + c)


