def solution(stdin: str) -> str:
    n = int(stdin.strip())

    if n == 1:
        return '1'
    elif n == 9:
        return '9'

    # Find the number of digits in n
    num_digits = 0
    while n > 0:
        num_digits += 1
        n //= 10

    # Count the number of digits that can be rounded
    num_round_digits = num_digits
    for i in range(1, num_digits):
        if n % 10!= 0:
            num_round_digits = i
            break
        n //= 10

    # Generate the list of round numbers
    round_numbers = []
    for i in range(num_round_digits):
        round_numbers.append(10 ** (num_digits - 1 - i))

    # Count the minimum number of terms needed to represent n as a sum of round numbers
    min_terms = 1
    for i in range(num_round_digits):
        if round_numbers[i] <= n:
            min_terms += 1
            n -= round_numbers[i]

    # Generate the list of terms
    terms = []
    for i in range(min_terms):
        terms.append(round_numbers[min_terms - 1 - i])

    # Add the remaining summands (if any) to the list of terms
    while n > 0:
        for i in range(len(round_numbers)):
            if n >= round_numbers[i]:
                terms.append(round_numbers[i])
                n -= round_numbers[i]
                break

    # Print the output
    output = str(min_terms) +''
    for term in terms:
        output += str(term) +''
    output += '\n'

    return output

