    N, M, X = map(int, stdin.readline().strip().split())
    A = list(map(int, stdin.readline().strip().split()))

    # Initialize variables
    min_cost = float('inf')
    visited = set()

    # DFS function to traverse all the squares
    def dfs(curr_square, cost):
        nonlocal min_cost
        visited.add(curr_square)
        if curr_square == 0 or curr_square == N:
            min_cost = min(min_cost, cost)
        else:
            for i in range(M):
                if A[i] == curr_square:
                    if curr_square - 1 not in visited:
                        dfs(curr_square - 1, cost + 1)
                    if curr_square + 1 not in visited:
                        dfs(curr_square + 1, cost)

    # Call the DFS function for the starting square
    dfs(X, 0)

    # Return the minimum cost
    return str(min_cost)

