def solution(stdin: str) -> str:
    n, s = map(int, stdin.readline().strip().split())
    s = stdin.readline().strip()

    # possible positions to cut the string
    cuts = [i for i in range(n)]

    # helper function to count the number of different letters in a substring
    def count_diff_letters(s):
        return len(set(s))

    # dynamic programming
    dp = [0] * n
    for i in range(n):
        max_diff = 0
        for j in range(i):
            # try all possible cuts to get a substring with maximum number of different letters
            for k in range(cuts[j]+1, cuts[i]):
                diff = count_diff_letters(s[j+1:k+1])
                if diff > max_diff:
                    max_diff = diff
        dp[i] = max_diff

    # find the largest possible number of different letters contained in both X and Y
    max_diff = 0
    for i in range(n):
        for j in range(i+1, n):
            diff = count_diff_letters(s[cuts[i]+1:cuts[j]+1])
            if diff > max_diff:
                max_diff = diff

    return str(max_diff)

