    # Compute the cheapest cost of travel between districts
    dist = [[float('inf')] * n for _ in range(n)]
    for x, y, w in roads:
        dist[x-1][y-1] = w
        dist[y-1][x-1] = w
    for k in range(n):
        for i in range(n):
            for j in range(n):
                dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j])

    # Compute the minimum total cost of courier routes
    total_cost = 0
    for a, b in routes:
        total_cost += dist[a-1][b-1]

    # Find the minimum total cost of courier routes with some road cost zero
    for i in range(m):
        x, y, w = roads[i]
        if x!= y and w > 0:
            roads[i] = (x, y, 0)
            total_cost_0 = 0
            for a, b in routes:
                total_cost_0 += dist[a-1][b-1]
            if total_cost_0 < total_cost:
                total_cost = total_cost_0
            roads[i] = (x, y, w)

