        # Left
        new_grid = [[0, 0, 0, 0] for _ in range(4)]
        for i in range(4):
            j = 0
            while j < 4:
                if grid[i][j]!= 0:
                    new_grid[i][j] = grid[i][j]
                    j += 1
                elif j < 3 and grid[i][j+1]!= 0:
                    new_grid[i][j] = grid[i][j+1]
                    new_grid[i][j+1] = 0
                    j += 2
                elif j > 0 and grid[i][j-1]!= 0:
                    new_grid[i][j] = grid[i][j-1]
                    new_grid[i][j-1] = 0
                    j += 2
                else:
                    j += 1
        grid = new_grid
    elif direction == 1:
        # Up
        new_grid = [[0, 0, 0, 0] for _ in range(4)]
        for j in range(4):
            i = 0
            while i < 4:
                if grid[i][j]!= 0:
                    new_grid[i][j] = grid[i][j]
                    i += 1
                elif i < 3 and grid[i+1][j]!= 0:
                    new_grid[i][j] = grid[i+1][j]
                    new_grid[i+1][j] = 0
                    i += 2
                elif i > 0 and grid[i-1][j]!= 0:
                    new_grid[i][j] = grid[i-1][j]
                    new_grid[i-1][j] = 0
                    i += 2
                else:
                    i += 1
        grid = new_grid
    elif direction == 2:
        # Right
        new_grid = [[0, 0, 0, 0] for _ in range(4)]
        for i in range(4):
            j = 3
            while j >= 0:
                if grid[i][j]!= 0:
                    new_grid[i][3-j] = grid[i][j]
                    j -= 1
                elif j > 0 and grid[i][j-1]!= 0:
                    new_grid[i][3-j] = grid[i][j-1]
                    new_grid[i][3-j-1] = 0
                    j -= 2
                elif j < 3 and grid[i][j+1]!= 0:
                    new_grid[i][3-j] = grid[i][j+1]
                    new_grid[i][3-j+1] = 0
                    j -= 2
                else:
                    j -= 1
        grid = new_grid
    elif direction == 3:
        # Down
        new_grid = [[0, 0, 0, 0] for _ in range(4)]
        for j in range(4):
            i = 3
            while i >= 0:
                if grid[i][j]!= 0:
                    new_grid[3-i][j] = grid[i][j]
                    i -= 1
                elif i > 0 and grid[i-1][j]!= 0:
                    new_grid[3-i][j] = grid[i-1][j]
                    new_grid[3-i-1][j] = 0
                    i -= 2
                elif i < 3 and grid[i+1][j]!= 0:
