def solution(stdin: str) -> str:
    # parse input
    n1, n2 = map(int, stdin.readline().split())
    orders1 = stdin.readline().strip().split()
    orders2 = stdin.readline().strip().split()
    t = int(stdin.readline().strip())

    # create ant objects
    ants = []
    for i in range(n1):
        ants.append(Ant(orders1[i], i))
    for i in range(n2):
        ants.append(Ant(orders2[i], n1+i))

    # simulate motion
    for i in range(t):
        for j in range(n1+n2):
            if j < n1:
                # ant from first row
                if j > 0:
                    if ants[j].direction == 'R' and ants[j-1].direction == 'L':
                        ants[j], ants[j-1] = ants[j-1], ants[j]
                ants[j].move()
            else:
                # ant from second row
                if j > n1:
                    if ants[j].direction == 'L' and ants[j-1].direction == 'R':
                        ants[j], ants[j-1] = ants[j-1], ants[j]
                ants[j].move()

    # print final order
    result = ''
    for ant in ants:
        result += ant.order
    return result

class Ant:
    def __init__(self, order, index):
        self.order = order
        self.index = index
        self.direction = 'L'

    def move(self):
        if self.direction == 'L':
            self.direction = 'R'
        else:
            self.direction = 'L'

