def solution(stdin: str) -> str:
    # Split the input into lines
    lines = stdin.split("\n")

    # Parse the first line as the first instruction
    first_line = lines[0]
    first_instr = first_line[0]

    # Parse the lines as instructions and parameters
    instr_params = []
    for line in lines[1:]:
        instr_params += [(line[0], line[1:])]

    # Count the number of parameters for each instruction
    param_counts = {}
    for instr, params in instr_params:
        if instr not in param_counts:
            param_counts[instr] = len(params)

    # Compute the number of NOP instructions needed
    nop_count = 0
    for instr, param_count in param_counts.items():
        # Skip the first instruction, which must start at address 0
        if instr == first_instr:
            continue

        # Compute the memory addresses of each instruction
        base_addr = instr_params.index((instr, '')) * 4
        instr_addrs = [base_addr + 4*i for i in range(param_count+1)]

        # Determine if each instruction needs to be padded with NOPs
        for addr in instr_addrs:
            if addr % 4!= 0:
                nop_count += 1
                break

    # Return the number of NOP instructions needed
    return str(nop_count)

