    queue = [(0, 0, 0, 1)]  # (row, col, time, zones)
    directions = {
        'U': (-1, 0),
        'D': (1, 0),
        'L': (0, -1),
        'R': (0, 1)
    }

    while queue:
        row, col, time, zones = queue.pop(0)

        if time >= t:
            return str(zones)

