        for d in directions:
            dr, dc = directions[d]
            nr, nc = row + dr, col + dc

            if grid[nr][nc] == '0':
                queue.append((nr, nc, time + 1, zones + 1))
            elif grid[nr][nc] == 'U':
                queue.append((nr, nc, time + 1, max(zones, abs(nr - row) + 1)))
            elif grid[nr][nc] == 'D':
