    res = []
    for i in range(n):
        row_sum = 0
        for j in range(n):
            row_sum ^= m[i][j]
        res.append(row_sum)

    # calculate the product of all elements in the first row
    product = 1
    for i in range(n):
        product *= res[i]

    # divide each element in the first row by the product
    for i in range(n):
        res[i] //= product

    return''.join(str(x) for x in res)

