def solution(stdin: str) -> str:
    """
    This function solves the problem by finding all the paths from the player’s starting position to the gold.
    It then calculates the number of paths that do not include traps.
    Finally, it returns the number of paths that do not include traps and the gold.
    """
    # Parse the input
    lines = stdin.split('\n')
    w, h = map(int, lines[0].split())
    map_ = []
    for line in lines[1:]:
        map_.append(line.strip())

    # Initialize variables
    player = (None, None)
    gold = []
    traps = []
    paths = []

    # Find the player’s starting position
    for i in range(h):
        for j in range(w):
            if map_[i][j] == 'P':
                player = (i, j)
                break
        if player is not None:
            break

    # Find the gold and traps
    for i in range(h):
        for j in range(w):
            if map_[i][j] == 'G':
                gold.append((i, j))
            elif map_[i][j] == 'T':
                traps.append((i, j))

    # Find all the paths from the player’s starting position to the gold
    def find_paths(start, visited, gold):
        if start == player:
            return [[start]]
        if start in visited:
            return []
        visited.add(start)
        paths = []
        for dir_ in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            new_pos = (start[0] + dir_[0], start[1] + dir_[1])
            if new_pos in gold:
                paths.append([start, new_pos])
            elif map_[new_pos[0]][new_pos[1]]!= '#':
                for path in find_paths(new_pos, visited, gold):
                    paths.append([start] + path)
        return paths

    for gold_pos in gold:
        paths += find_paths(player, set(), [gold_pos])

    # Count the number of paths that do not include traps
    safe_paths = 0
    for path in paths:
        safe = True
        for pos in path:
            if pos in traps:
                safe = False
                break
        if safe:
            safe_paths += 1

    # Return the number of paths that do not include traps and the gold
    return f"{safe_paths} {len(gold)}"

