    # parse input
    n, m = map(int, stdin.split())
    grid = []
    for i in range(n):
        grid.append(list(stdin.readline().strip()))
    # find all adjacent cells
    adj = []
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.':
                for di, dj in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                    ni, nj = i + di, j + dj
                    if 0 <= ni < n and 0 <= nj < m and grid[ni][nj] == '.':
                        adj.append((i, j, ni, nj))
    # dfs to find all valid placements
    valid = []
    def dfs(i, j, used):
        if i < 0 or i >= n or j < 0 or j >= m or grid[i][j] == '#' or (i, j) in used:
            return
        used.add((i, j))
        if grid[i][j] == '.':
            for ni, nj in adj:
                if ni == i and nj == j or (ni, nj) in used:
                    continue
                dfs(ni, nj, used)
        else:
            for ni, nj in adj:
                if ni == i and nj == j or (ni, nj) in used:
                    continue
                dfs(ni, nj, used)
                valid.append(used)
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.':
                used = set()
                dfs(i, j, used)
    # find the placement that maximizes profit
    max_profit = 0
    max_placement = None
    for placement in valid:
        profit = sum(grid[i][j] == '.' for i, j in placement)
        if profit > max_profit:
            max_profit = profit
            max_placement = placement
    # update grid with the best placement
    for i, j in max_placement:
        grid[i][j] = 'E'
    # print output
    output = ''
    for row in grid:
        output += ''.join(row) + '\n'
    return output

