def solution(stdin: str) -> str:
    # read input
    m = int(stdin.strip())
    # initialize variables
    n = 1
    while n*n*n < m:
        n += 1
    if n*n*n == m:
        return str(n-1) +'' + str(n)
    if n*n*n > m:
        return 'none'
    # compute bus numbers
    bus_numbers = []
    for i in range(n-1, 1, -1):
        for j in range(i-1, 0, -1):
            if m - i*i*i - j*j*j >= 0:
                bus_numbers.append((i,j))
                m -= i*i*i + j*j*j
                if m <= 0:
                    return''.join(str(b) for b in bus_numbers)
    return 'none'

