    visited = [[False for _ in range(n)] for _ in range(n)]

    # Define a function to check if a position is valid
    def is_valid(row: int, col: int) -> bool:
        return row >= 1 and row <= n and col >= 1 and col <= n and not visited[row-1][col-1] and board[row-1][col-1]!= '#'

    # Define a function to check if the Knight can reach the destination
    def can_reach_destination(row: int, col: int, steps: int) -> bool:
        if row == 1 and col == 1:
            return True
        # Check all possible moves for the knight
        for move in [(row + 2, col + 1), (row + 2, col - 1), (row - 2, col + 1), (row - 2, col - 1), (row + 1, col + 2), (row + 1, col - 2), (row - 1, col + 2), (row - 1, col - 2)]:
            if is_valid(move[0], move[1]):
                if can_reach_destination(move[0], move[1], steps + 1):
                    return True
        return False

    # Call the can_reach_destination function for the initial position of the knight
    if can_reach_destination(1, 1, 0):
        return str(can_reach_destination(1, 1, 0))
    else:
        return '-1'

