def solution(stdin: str) -> str:
    # Get key phrase and plaintext
    key_phrase, plaintext = stdin.strip().split('\n')

    # Remove spaces and duplicate letters from key phrase
    key_phrase = ''.join(sorted(set(key_phrase.replace(' ', '').lower())))

    # Fill in the key table with the letters of the key phrase
    key_table = [[''] * 5 for _ in range(5)]
    for i, letter in enumerate(key_phrase):
        key_table[i // 5][i % 5] = letter

    # Fill the remaining spaces with the rest of the letters of the alphabet
    alphabet = [chr(i) for i in range(ord('a'), ord('z') + 1)]
    for i, letter in enumerate(alphabet):
        if letter in key_phrase:
            continue
        key_table[i // 5][i % 5] = letter
        if i == 24:
            break

    # Encrypt the plaintext
    ciphertext = ''
    for i in range(0, len(plaintext), 2):
        if plaintext[i] == 'x' or plaintext[i+1] == 'x':
            continue
        plaintext_digraph = plaintext[i:i+2].lower()
        if plaintext_digraph[0] == plaintext_digraph[1]:
            ciphertext += key_table[key_table.index(plaintext_digraph[0])][-1] + 'x'
        elif key_table[0].index(plaintext_digraph[0]) == key_table[0].index(plaintext_digraph[1]):
            ciphertext += key_table[key_table.index(plaintext_digraph[0])][(key_table[0].index(plaintext_digraph[0]) + 1) % 5] + key_table[key_table.index(plaintext_digraph[0])][(key_table[0].index(plaintext_digraph[1]) + 1) % 5]
        elif key_table[0].index(plaintext_digraph[0]) == key_table.index(plaintext_digraph[1]):
            ciphertext += key_table[key_table.index(plaintext_digraph[0])][(key_table[0].index(plaintext_digraph[0]) + 1) % 5] + key_table[key_table.index(plaintext_digraph[1])][(key_table[0].index(plaintext_digraph[1]) + 1) % 5]
        else:
            ciphertext += key_table[key_table.index(plaintext_digraph[0])][key_table[0].index(plaintext_digraph[1])] + key_table[key_table.index(plaintext_digraph[1])][key_table[0].index(plaintext_digraph[0])]

    return ciphertext.upper()

