def solution(stdin: str) -> str:
    events = stdin.split()
    shares = 0
    total_cost = 0
    for event in events:
        if event.startswith("buy"):
            num_shares, cost_per_share = map(int, event[4:].split())
            shares += num_shares
            total_cost += num_shares * cost_per_share
        elif event.startswith("sell"):
            num_shares, sell_price = map(int, event[5:].split())
            shares -= num_shares
            total_cost -= num_shares * sell_price
        elif event.startswith("split"):
            num_new_shares = int(event[6:])
            shares *= num_new_shares
        elif event.startswith("merge"):
            num_merged_shares = int(event[6:])
            shares /= num_merged_shares
        elif event.startswith("die"):
            sell_price = int(event[4:])
            break
    else:
        sell_price = total_cost / shares
    final_sell_price = sell_price * (1 - 0.3)
    return "{:.7f}".format(final_sell_price)

