def solution(stdin: str) -> str:
    # input
    A, B, C, D = map(int, stdin.strip().split())

    # first move
    x, y = A, 0
    max_area = 0

    # check all four directions
    for i in range(4):
        # if A is the largest
        if i % 2 == 0:
            if A >= B and A >= C and A >= D:
                x, y = A, 0
            elif B >= A and B >= C and B >= D:
                x, y = 0, B
            elif C >= A and C >= B and C >= D:
                x, y = -B, C
            else:
                x, y = -C, D
        # if B is the largest
        elif i % 2 == 1:
            if B >= A and B >= C and B >= D:
                x, y = 0, B
            elif A >= B and A >= C and A >= D:
                x, y = A, 0
            elif C >= A and C >= B and C >= D:
                x, y = -B, C
            else:
                x, y = -C, D

        # move according to current direction
        for j in range(A, 0, -1):
            if x >= 0:
                y += j
            else:
                y -= j
            max_area = max(max_area, j * abs(y))

    return max_area

