def solution(stdin: str) -> str:
    n, m = map(int, stdin.strip().split())
    s = []
    for _ in range(m):
        s.append(map(int, stdin.strip().split()))

    def can_prepare(s, i, j):
        if s[i] >= j:
            return False
        if i == 0:
            return True
        return s[i - 1] > j

    def can_pass(s, i, j):
        if s[i] > j:
            return False
        if i == m - 1:
            return True
        return s[i + 1] < s[i]

    def can_relax(s, i, j):
        if s[i] < j:
            return False
        if i == m - 1:
            return True
        return s[i + 1] > s[i]

    def schedule(s, i, j):
        if not can_prepare(s, i, j):
            return [-1, -1, -1]

        max_day = max(s[i], s[i + 1])
        min_day = min(s[i], s[i + 1])

        if can_pass(s, i, j):
            return [max_day, s[i], 0]
        elif can_relax(s, i, j):
            return [min_day, s[i], 0]
        else:
            return [-1, -1, -1]

    schedule_list = []
    for i in range(m):
        for j in range(max(s[i], s[i + 1]), min(n, s[i + 1])):
            if schedule(s, i, j)!= [-1, -1, -1]:
                schedule_list.append(schedule(s, i, j))

    if schedule_list == []:
        return '-1'

    schedule_list.sort(key=lambda x: x[0])
    for i in range(m):
        for j in range(max(s[i], s[i + 1]), min(n, s[i + 1])):
            if schedule(s, i, j)!= [-1, -1, -1]:
                if schedule(s, i, j)[0] == i + 1:
                    print(schedule(s, i, j)[1:], end=' ')
                else:
                    print(schedule(s, i, j)[1], end=' ')
                if i == m - 1:
                    print()
                break

    return ''

