def solution(stdin: str) -> str:
    n = int(stdin.readline().strip())
    a = list(map(int, stdin.readline().strip().split()))

    # Build a stack of ravioli for each number
    stack = []
    for i in range(n):
        while stack and a[i] > stack[-1][1]:
            stack.pop()
        stack.append((a[i], i))

    # Find the tallest stack
    tallest = float('-inf')
    for stack in stack:
        if stack[0][0] > tallest:
            tallest = stack[0][0]

    # Remove the tallest stack and add its height to the output array
    output = []
    for stack in stack:
        if stack[0][0] == tallest:
            output.append(stack[0][1])
            del stack[0]

    # Shift the stacks in the row so that there is no gap between them
    while stack:
        output.append(stack.pop()[1])

    # Check if the output array is sorted
    if not output or output[-1]!= n - 1:
        return "NO"
    return "YES"

