    N, M = map(int, stdin.split())
    graph = [[0] * N for _ in range(N)]

    for i in range(M):
        a, b = map(int, stdin.split())
        graph[a - 1][b - 1] = 1
        graph[b - 1][a - 1] = 1

    def has_cycle(start: int) -> bool:
        visited = [False] * N
        queue = [start]
        while queue:
            curr = queue.pop(0)
            visited[curr] = True

            for neighbor in graph[curr]:
                if not visited[neighbor]:
                    queue.append(neighbor)
                    return True

        return False

    def dfs(curr: int, path: List[int]) -> int:
        visited = [False] * N
        if curr == 1:
            path.append(curr)
            if len(path) == N:
                return 1
            path.pop()
            return 0
        else:
            visited[curr] = True
            for neighbor in graph[curr]:
                if not visited[neighbor]:
                    return dfs(neighbor, path)
            path.pop()
            visited[curr] = False
            return 0

    def count_paths() -> int:
        count = 0
        path = []
        for i in range(N):
            if not visited[i]:
                count += dfs(i, path)
        return count

    return str(count_paths())

