    tree = [[] for _ in range(n)]
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    def dfs(visited, curr, tree, total_weight):
        if curr == n:
            return total_weight

        if curr in visited:
            return 0

        visited.add(curr)
        total_weight += tree[curr][0]

        for neighbor in tree[curr]:
            total_weight = dfs(visited, neighbor, tree, total_weight)

        return total_weight

    visited = set()
    max_total_weight = dfs(visited, 1, tree, 0)
    max_total_weight -= k

    return str(max_total_weight)

