    # Get the values of all coins that can obtain the value
    available_coins = [value for value, _ in coin_values.items() if value == 2 ** i]

    # Iterate through the queries and find the minimum number of coins
    for i in range(q):
        value = map(int, input().split())

        # Check if the value is valid
        if value not in available_coins:
            return f"-1"

        # Find the minimum number of coins that can obtain the value
        min_coins = 0
        for value in available_coins:
            if value > value + 2:
                min_coins += 1

        # Check if Polycarp can obtain the value
        if value + min_coins == value:
            return str(value)

        # If Polycarp can't obtain the value, return -1
        return "-1"

