    moves = [int(s) for s in stdin.strip()]
    min_length = min(n, k)
    moves.append(min_length)

    def backtrack(start: int, memo: List[int] = None) -> str:
        if memo is None:
            memo = [0] * (n + 1)
        memo[start] += 1

        if memo[start] == k:
            return ''.join([chr(i + ord('0')) for i in range(min_length)])

        for i in range(start, n - 1, 2):
            if i + 1 < n and memo[i + 1] == 0:
                memo[i + 1] += 1
                path = backtrack(i + 1, memo)
                memo[i + 1] -= 1
                memo[i] = 0
                return path + '0'
            elif i + 2 < n and memo[i + 2] == 0:
                memo[i + 2] += 1
                path = backtrack(i + 2, memo)
                memo[i + 2] -= 1
                memo[i] = 0
                return path + '1'

        if memo[n - 1] == k:
            return ''.join([chr(i + ord('0')) for i in range(min_length)])

        for i in range(n - 2, -1, -2):
            if i - 1 >= 0 and memo[i - 1] == 0:
                memo[i - 1] += 1
                path = backtrack(i - 1, memo)
                memo[i - 1] -= 1
                memo[i] = 0
                return path + '0'
            elif i - 2 >= 0 and memo[i - 2] == 0:
                memo[i - 2] += 1
                path = backtrack(i - 2, memo)
                memo[i - 2] -= 1
                memo[i] = 0
                return path + '1'

        return ''

    return backtrack(0)

