def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    s = stdin.split()[1]

    def remove_substring(s, start, end):
        if start >= end:
            return ''
        return s[start+1:end]

    def optimization(s):
        i = 0
        while i < len(s):
            j = i + 1
            while j < len(s) and s[j]!= 'D':
                j += 1
            if j - i > 1:
                s = s[:i] + remove_substring(s, i, j-1) + s[j:]
            i = j
        return s

    optimized_s = optimization(s)
    if optimized_s == '':
        return '-1'

    l, r = 0, len(optimized_s) - 1
    while l < r:
        mid = (l + r) // 2
        if optimized_s[mid] == 'L':
            l = mid + 1
        elif optimized_s[mid] == 'R':
            r = mid - 1
        elif optimized_s[mid] == 'U':
            mid += 1
        else:
            mid -= 1

    return str(l) +'' + str(r)


