    a = list(map(int, stdin.split()[1:]))

    # Check if all skills are distinct
    if len(set(a))!= n:
        return "0"

    # Check if all skills are equal
    if len(a)!= len(set(a)):
        return "0"

    # Count the number of students with unique skills
    unique_skills = {}
    for skill in a:
        if skill not in unique_skills:
            unique_skills[skill] = 1
        else:
            unique_skills[skill] += 1

    # Count the number of students with the same skills
    same_skills = {}
    for skill in a:
        if skill in same_skills:
            same_skills[skill] += 1
        else:
            same_skills[skill] = 1

    # Find the max number of teams
    max_teams = 0
    for i in range(n):
        num_students = unique_skills[a[i]]
        if i == 0:
            max_teams = max(max_teams, num_students)
        else:
            if same_skills[a[i]] == num_students:
                max_teams = max(max_teams, num_students * 2)
            else:
                max_teams = max(max_teams, num_students)

    return str(max_teams)

def solution(stdin: str) -> str:
    n = int(stdin.readline().strip())
    edges = []
    for _ in range(n - 1):
        a, b = map(int, stdin.readline().strip().split())
        edges.append((a - 1, b - 1))

    def dfs(curr, dist, path, max_cost, memo):
        if len(path) == n:
            max_cost = min(max_cost, sum(dist[i] * a for i, a in enumerate(path)))
            return max_cost

        for i in range(n):
            if i == curr:
                continue

            if memo[i]:
                continue

            dist[i] += 1
            path.append(i + 1)
            max_cost = max(max_cost, dfs(i, dist, path, max_cost, memo) + dist[i] * a)
            path.pop()
            dist[i] -= 1

        return max_cost

    memo = [False] * n
    dist = [0] * n
    path = [0] * n
    max_cost = 0
    for u, v in edges:
        max_cost = max(max_cost, dfs(u, dist, path, 0, memo))
    return str(max_cost)

