    # Assuming that the sheet of paper is locked
    lock = False

    def dfs(node, visited):
        visited[node] = True
        if lock:
            return

        for child in graph[node]:
            if not visited[child]:
                dfs(child, visited)

    graph = {}
    for line in stdin.split('\n'):
        if not line.strip():
            continue

        substrings = line.split(T)
        if len(substrings) == 1:
            substrings = [substrings[0]]

        for substring in substrings:
            if not substring or not substring.islower():
                continue

            heapq.heappush(graph[node], substring)

    if not graph:
        return "UNRESTORABLE"

    node = heapq.heappop(graph[T])
    visited = [False] * len(graph)
    dfs(node, visited)

    result = ""
    for substring in graph[node]:
        if substring == T:
            continue
        result += substring

    return result

