    num_speeds = 0
    for i in range(1, len(T)):
        num_speeds += (T[i] - T[i - 1]) * (X[i] - X[i - 1])

    # Find the second milestone
    for i in range(len(T)):
        if T[i] > T[i - 1]:
            continue
        for j in range(i + 1, len(T)):
            if T[j] - T[i] > 2:
                break
