        if primes[i]:
            for j in range(i*i, N + 1, i):
                primes[j] = False

    # Find the smallest number not already crossed out
    P = min(prime for prime in primes if not primes[prime])

    # Cross out P and all its multiples that aren't already crossed out
    for i in range(P*P, N + 1, P):
        primes[i] = False

