        groups = {}
        for i, vote in enumerate(votes):
            if vote in groups:
                groups[vote].append(i)
            else:
                groups[vote] = [i]
        return groups

    party_votes = group_votes(precincts[0])
    for precinct in precincts[1:]:
        party_votes.update(group_votes(precinct))

    def determine_winners(party_votes, districts):
        winners = []
        for district in districts:
            for candidate, votes in party_votes.items():
                if district in votes:
                    candidate_votes = [districts.index(vote) for vote in votes[district]]
                    if candidate_votes.count(candidate) >= votes[district].count(candidate):
                        winners.append(candidate)
        return winners

    def determine_wasted_votes(winners, precincts, districts):
        wasted_votes = {}
        for precinct in precincts:
            for candidate, votes in party_votes.items():
                if precinct in votes:
                    wasted_votes[candidate] = set()
                    for district in districts:
                        if district in votes:
                            district_votes = [districts.index(vote) for vote in votes[district]]
                            if precinct in district_votes:
                                for i in range(len(district_votes)):
                                    for j in range(i + 1, len(district_votes)):
                                        if i!= j:
                                            wasted_votes[candidate].add((districts.index(district_votes[i]), districts.index(district_votes[j])))
        return wasted_votes

    def determine_efficiency_gap(wasted_votes):
        efficiency_gap = 0
        for candidate, wasted_votes_for_candidate in wasted_votes.items():
            efficiency_gap += len(wasted_votes_for_candidate)
        return abs(efficiency_gap) / len(wasted_votes)

    winners = determine_winners(party_votes, districts)
    wasted_votes = determine_wasted_votes(winners, precincts, districts)
    efficiency_gap = determine_efficiency_gap(wasted_votes)
