    while low <= high:
        mid = (low + high) // 2
        if (a[mid - 1] ** 2 + sum([a[i] ** 2 for i in range(mid, n)]) + a[n - 1] ** 2) * (a[mid] + sum([a[i] for i in range(mid, n)]) + a[n - 1]) >= 0:
