    heights = []
    for _ in range(M):
        heights.append(list(map(int, stdin.split())))

    def dfs(x: int, y: int) -> int:
        if 0 <= x < M and 0 <= y < N:
            if heights[x][y] > 0:
                heights[x][y] -= 1
                if heights[x][y] == 0:
                    return dfs(x + 1, y) + 1
                else:
                    return dfs(x, y + 1)
        return 0

    start_x, start_y = 0, 0
    end_x, end_y = M - 1, N - 1
    min_ladder_length = 0

    while start_x < end_x and start_y < end_y:
        min_ladder_length = min(min_ladder_length, dfs(start_x, start_y))
        start_x += 1
        start_y += 1
        end_x -= 1
        end_y -= 1

    return str(min_ladder_length)

