    def find_matrix_elements(matrix, i, j):
        result = []
        for k in range(i, j):
            if matrix[k][j] & matrix[i][k] == matrix[i][k]:
                result.append(matrix[k][j])
        return result

    def reconstruct_matrix_elements(matrix, i, j):
        result = []
        for k in range(i, j):
            result.append(matrix[k][j])
        return result

    i, j = 0, n - 1
    while i < j:
        matrix_elements = find_matrix_elements(matrix, i, j)
        matrix_elements.sort()
        reconstructed_elements = reconstruct_matrix_elements(matrix, i, j)
