        return "use the stairs"

    def path(i: int) -> int:
        if i >= f:
            return 0
        elif i <= s:
            return 1
        else:
            return path(i-1) + 1

    push_count = []
    for i in range(s, g+1):
        push_count.append(path(i))

    min_count = float('inf')
    min_index = -1

    for i in range(1, f):
        push_count[i] = push_count[i-1] + push_count[i-2] + push_count[i-3]

    for i in range(g, f):
        push_count[i] = push_count[i-1] + push_count[i-2] + push_count[i-3]

    for i in range(1, f):
        for j in range(i+1, f):
            push_count[i] = min(push_count[i], push_count[i-1] + push_count[j-1])

    for i in range(g, f):
        for j in range(i+1, f):
            push_count[i] = min(push_count[i], push_count[i-1] + push_count[j-1])

    for i in range(f-1, g-1, -1):
        if push_count[i] < min_count:
            min_count = push_count[i]
            min_index = i

    return min_index + 1

