    start_times = [0] * M
    completed_times = [0] * M

    for i in range(N):
        for j in range(M):
            start_times[j] += stages[j][i]
            completed_times[j] += stages[j][i]

    time_taken = [0] * N
    time_taken[0] = 0

    for i in range(1, N):
        min_time = float('inf')
        min_swath = -1

        for j in range(M):
            if time_taken[j] < min_time and completed_times[j] < stages[j][i - 1]:
                min_time = time_taken[j]
                min_swath = j

        time_taken[i] = min_time + start_times[min_swath]
        start_times[min_swath] += stages[min_swath][i - 1]

    return''.join(map(str, time_taken))

