def solution(stdin: str) -> str:
    n = int(stdin.strip())
    polygons = []

    for _ in range(n):
        x, y = map(float, stdin.strip().split())
        polygons.append((x, y))

    area = 0
    for i in range(n):
        for j in range(i + 1, n):
            x1, y1 = polygons[i]
            x2, y2 = polygons[j]
            area += (x2 - x1) * (y1 + y2) - (y2 - y1) * (x1 + x2)

    if area < 0:
        area = 0

    area = abs(area) / 20000000

    polygons = [polygon + (area, area) for polygon in polygons]
    polygons = [(polygon[0] - area, polygon[1]) for polygon in polygons]
    polygons = [(polygon[0], polygon[1] + area) for polygon in polygons]

    max_x = max(polygon[0] for polygon in polygons)
    min_x = min(polygon[0] for polygon in polygons)
    max_y = max(polygon[1] for polygon in polygons)
    min_y = min(polygon[1] for polygon in polygons)

    shifted_polygons = [(polygon[0] + min_x, polygon[1] + min_y) for polygon in polygons]

    for polygon in shifted_polygons:
        if polygon[0] <= max_x and polygon[0] >= min_x and polygon[1] <= max_y and polygon[1] >= min_y:
            continue
        else:
            return '\n'.join(map(str, polygon))

