    correct = 0

    for _ in range(n):
        line = stdin.strip()
        assumptions = line.split()[1:]
        arrow = line.split("->")[1]
        conclusion = line.split("->")[2]

        if assumptions == []:
            proof.append((arrow, conclusion))
            continue

        for assumption in assumptions:
            if assumption not in ["A", "B", "C", "D", "E"]:
                return f"error: {assumption} is not a valid assumption"

        proof.append((arrow, conclusion))
        correct += 1

    if correct == n:
        return "correct"
    else:
        return f"error: line {proof[correct - 1]}"


