def solution(stdin: str) -> str:
    n, m = map(int, stdin.strip().split())
    grid = ['.'] * n
    for i in range(m):
        grid[i] = grid[i][:i] + grid[i][i+1:]
    visited = set()
    def dfs(x, y):
        if (x, y) in visited:
            return False
        visited.add((x, y))
        if grid[x][y] == '.':
            grid[x][y] = 'E'
        elif grid[x][y] == '#':
            grid[x][y] = 'E'
            return True
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < m and grid[nx][ny] == '.':
                grid[nx][ny] = 'E'
                if dfs(nx, ny):
                    return True
        return False
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'E':
                if dfs(i, j):
                    return ''.join(grid[i][j] for j in range(m))
    return 'No solution found'

