        seats_used = [False] * S
        for seat in range(S):
            if seats_used[seat]:
                continue

            i, j = row, seat
            while True:
                if i > 0 and seats_used[i - 1]:
                    i -= 1
                    continue
                if j > 0 and seats_used[j - 1]:
                    j -= 1
                    continue
                if i < R - 1 and seats_used[i + 1]:
                    i += 1
                    continue
                if j < S - 1 and seats_used[j + 1]:
                    j += 1
                    continue
                else:
                    break

            if seats_used[j]:
                total_handshakes += 1
                seats_used[seat] = True
                if row == 0 or seat == 0 or row == R - 1 or seat == S - 1:
                    return str(total_handshakes)
                continue

    return str(total_handshakes)

